<script setup lang="ts">
import { ref } from 'vue'
import axios from 'axios'

const jumlahBaduta = ref<number | null>(null)
const jumlahCatin = ref<number | null>(null)
const jumlahBumil = ref<number | null>(null)
const jumlahPascaPersalinan = ref<number | null>(null)
const kecamatan = ref<string | null>(null)

const handleClick = async (namaKecamatan: string) => {
  try {
    const response = await axios.get(`wilayah/${namaKecamatan}`)
    jumlahBaduta.value = response.data.jumlah_baduta
    jumlahCatin.value = response.data.jumlah_catin
    jumlahBumil.value = response.data.jumlah_bumil
    jumlahPascaPersalinan.value = response.data.jumlah_pasca_persalinan
    kecamatan.value = response.data.kecamatan
  } catch (error) {
    console.error('Gagal mengambil data wilayah:', error)
  }
}

const lihatDetail = () => {
  if (!kecamatan.value) return
  const namaFile = kecamatan.value.charAt(0).toUpperCase() + kecamatan.value.slice(1).toLowerCase()
  window.location.href = `/sebaran/${namaFile}`
}

</script>

