    <button @click="kecamatan = null" class="text-red-500 font-bold hover:text-red-700 transition ml-auto">X</button>
  </div>
  <h2 class="text-sm font-semibold text-gray-800">Data Wilayah: {{ kecamatan }}</h2>
  <ul class="mt-2 space-y-1 text-sm font-normal text-gray-600">
    <li>Jumlah Baduta: <span class="font-semibold">{{ jumlahBaduta ?? '-' }}</span></li>
    <li>Jumlah Catin: <span class="font-semibold">{{ jumlahCatin ?? '-' }}</span></li>
    <li>Jumlah Bumil: <span class="font-semibold">{{ jumlahBumil ?? '-' }}</span></li>
    <li>Jumlah Pasca Persalinan: <span class="font-semibold">{{ jumlahPascaPersalinan ?? '-' }}</span></li>
  </ul>

  <button @click="lihatDetail" class="mt-4 text-sm font-normal bg-[#071556] text-white p-1 rounded-sm shadow hover:bg-[#071556] transition mx-auto block">
    Lihat Detail
  </button>
</div>
</template>